import {Box, Fade, Grid, Typography} from "@mui/material";
import "./AboutMe.css";
import fakePic from "../asset/rich_b.jpeg";
import {useRef} from "react";
import useOnScreen from "./useOnScreen";


const bio: string = "Lorem ipsum dolor sit amet, consectetur adipiscing elit." +
    " Phasellus ut tempus neque, eu tempus velit. Curabitur venenatis eu velit " +
    "eu bibendum. In hac habitasse platea dictumst. Curabitur congue, enim at " +
    "scelerisque tristique, turpis diam convallis augue, ac pretium ipsum mi " +
    "vel nunc. Duis nec justo vel nulla vestibulum bibendum sed ac nulla. Nullam " +
    "placerat id nunc ac ullamcorper. Fusce non magna turpis. Aliquam erat " +
    "volutpat. Aliquam mi sem, lacinia sed dolor at, accumsan venenatis mi. " +
    "Curabitur vel magna tristique, efficitur ante sit amet, consectetur odio. " +
    "In massa nulla, mattis ut efficitur mattis, vulputate id est. Vestibulum " +
    "vitae pharetra libero. Vestibulum tincidunt eu nisi sit amet lobortis. "
export default function AboutMe() {
    const ref = useRef()
    const isVisible = useOnScreen(ref)

    return (
        <Box
            id={"about"}
            ref={ref}
            sx={{
                minWidth: 500,
                maxWidth: "70%",
                // paddingLeft:20,
                paddingBottom: 10,
                paddingTop: 10,
            }}>

                <Box>
                    <Typography variant={"subtitle1"}>
                        <span className={"green"}>01.</span>About Me
                    </Typography>
                </Box>
                <Box
                    sx={{
                        display: "flex",
                    }}>
                    <Grid container spacing={2} sx={{}}>
                        <Grid item sm={12} md={6}>

                            <Typography variant={"body1"}
                                        style={{whiteSpace: 'pre-line'}}
                                        sx={{marginLeft: 6}}>
                                {bio}
                            </Typography>

                        </Grid>
                        <Grid item sm={12} md={6}
                              sx={{display: "flex", justifyContent: "center"}}>
                            <Box
                                sx={{
                                    maxWidth: "70%",
                                }}>
                                <Box
                                    sx={{
                                        backgroundColor: "#EF7B45",

                                        boxShadow: "2rem 2rem #4C956C"
                                    }}>
                                    <Fade in={isVisible}>
                                    <Box component={"img"} src={fakePic}
                                         sx={{}}/>
                                    </Fade>
                                </Box>

                            </Box>
                        </Grid>

                    </Grid>

                </Box>

        </Box>
    )
}
