import {createTheme} from "@mui/material";
import "./theme.css"
const theme= createTheme({
    typography:{
        fontFamily: "Space Mono",
        h1:{
            fontSize: "2rem",
            fontWeight: 500
        },
        body:{
            fontSize: 1,
        }
    }
})

export default theme
