import {Box, keyframes, Typography} from "@mui/material";
import {useState} from "react";
import ProjectCard from "./ProjectCard";

export default function Projects() {
    const selectPushToRight = keyframes`
  from {
    padding-left: 0;
    box-shadow:;
    color:;
  }

  to {
    padding-left: 1rem;
    box-shadow: -5px 0px 0px 0px #4C956C;
    color: #4C956C;

  }
`;

    const onHoverStyle = (n:number) => {
        return {
            "&:hover": {
                backgroundColor: "#EF7B45",
                transition: "background-color 1000ms linear;"
            },
            cursor: "pointer",
            animation: (selected == n) ? `${selectPushToRight} 1s ease forwards` : "",
        }
    };

    const [selected, setSelected] = useState<number>(0);
    return (
        <Box
            id={"projects"}
            sx={{
                marginTop: 10,
                minWidth: "50%",
                maxWidth: "70%",
            }}>
            <Box>
                <Typography variant={"subtitle1"}>
                    <span className={"green"}>02.</span>Projects
                </Typography>
            </Box>
            <Box sx={{display: "flex", width: 800}}>
                <Box sx={{width: "30%"}}>
                    <Box
                        onClick={() => {
                            setSelected(0)
                        }}
                        sx={{
                            ...onHoverStyle(0),
                        }}>
                        <Typography variant="body1">
                            Product Design:
                        </Typography>
                        <Typography variant="body1">
                            Winno
                        </Typography>
                    </Box>
                    <Box
                        onClick={() => {
                            setSelected(1)
                        }}
                        sx={{
                            ...onHoverStyle(1),
                        }}>
                        <Typography variant="body1">
                            Responsive Redesign:
                        </Typography>
                        <Typography variant="body1">
                            Poshmark
                        </Typography>
                    </Box>
                    <Box
                        onClick={() => {
                            setSelected(2)
                        }}
                        sx={{
                            ...onHoverStyle(2),
                        }}>
                        <Typography variant="body1">
                            Chrome Extension Dev:
                        </Typography>
                        <Typography variant="body1">
                            Sift
                        </Typography>
                    </Box>
                </Box>
                <Box sx={{width: "70%"}}>

                    <ProjectCard projectIndex={selected}/>
                </Box>
            </Box>
        </Box>
    )
}
